// /client/src/pages/Projects.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Projects.css';

const Projects = ({ user }) => {
    const [projects, setProjects] = useState([]);

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/users/${user.id}`);
                setProjects(response.data.projects);
            } catch (error) {
                console.error('Error fetching projects:', error);
            }
        };
        fetchUserData();
    }, [user.id]);

    return (
        <div className="page-container projects-page">
            <header className="page-header">
                <h2>My Projects</h2>
                <p>View details of the projects you are contributing to.</p>
            </header>
            
            <div className="project-grid">
                {projects.length > 0 ? (
                    projects.map(project => (
                        <div key={project.id} className="project-card">
                            <div className="project-header">
                                <h3>{project.name}</h3>
                                <span className={`status-badge ${project.status.toLowerCase().replace(' ', '-')}`}>{project.status}</span>
                            </div>
                            <p>{project.description}</p>
                            <div className="project-members">
                                <i className="fas fa-users"></i>
                                <span>Team: ...</span>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>No projects assigned yet.</p>
                )}
            </div>
        </div>
    );
};

export default Projects;
