// /client/src/pages/Projects.js
import React, { useState } from 'react';
import axios from 'axios';
import './Projects.css';

const Projects = ({ user }) => {
    const [userProjects, setUserProjects] = useState(user.projects);
    const [allUsers, setAllUsers] = useState(user.allUsers); // Assumes we could pass all users

    const getProjectMembers = (projectId) => {
        // This function would ideally fetch all users from an API or a global state
        // For this example, we'll hardcode it based on our JSON structure
        const hardcodedUsers = [
          {id: 1, username: "aman_sharma", projects: [201]},
          {id: 2, username: "priya_gupta", projects: [201]},
          {id: 3, username: "rahul_verma", projects: [202]},
          {id: 4, username: "sita_singh", projects: [203]},
          {id: 5, username: "vijay_kumar", projects: [204]},
          {id: 6, username: "meera_das", projects: [201]},
          {id: 7, username: "alok_patel", projects: [202]},
          {id: 8, username: "isha_jain", projects: [203]},
          {id: 9, username: "rohan_gupta", projects: [204]},
          {id: 10, username: "pooja_shah", projects: [201]},
        ];

        return hardcodedUsers
            .filter(u => u.projects.includes(projectId))
            .map(u => u.username)
            .join(', ');
    };
    
    return (
        <div className="page-container projects-page">
            <header className="page-header">
                <h2>My Projects</h2>
                <p>View details of the projects you are contributing to.</p>
            </header>
            
            <div className="project-grid">
                {userProjects.length > 0 ? (
                    userProjects.map(project => (
                        <div key={project.id} className="project-card">
                            <div className="project-header">
                                <h3>{project.name}</h3>
                                <span className={`status-badge ${project.status.toLowerCase().replace(' ', '-')}`}>{project.status}</span>
                            </div>
                            <p className="project-description">{project.description}</p>
                            <div className="project-members">
                                <i className="fas fa-users"></i>
                                <span>Team: {getProjectMembers(project.id)}</span>
                            </div>
                            <div className="project-deadline">
                                <i className="fas fa-calendar-alt"></i>
                                <span>Deadline: {project.deadline}</span>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>No projects assigned yet.</p>
                )}
            </div>
        </div>
    );
};

export default Projects;
