// /client/src/pages/Projects.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Projects.css';

const Projects = ({ user }) => {
    const [userProjects, setUserProjects] = useState([]);
    const [allUsers, setAllUsers] = useState([]);
    const [allProjects, setAllProjects] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const userResponse = await axios.get(`http://localhost:5000/api/users/${user.id}`);
                const allUsersResponse = await axios.get('http://localhost:5000/api/users');
                const allProjectsResponse = await axios.get('http://localhost:5000/api/projects');

                setAllUsers(allUsersResponse.data);
                setAllProjects(allProjectsResponse.data);
                
                const userProjectIds = userResponse.data.projects;
                const userProjects = allProjectsResponse.data.filter(p => userProjectIds.includes(p.id));
                setUserProjects(userProjects);

                setLoading(false);
            } catch (error) {
                console.error('Error fetching data:', error);
                setLoading(false);
            }
        };
        fetchData();
    }, [user.id]);

    const getProjectMembers = (projectId) => {
        return allUsers
            .filter(u => u.projects.includes(projectId))
            .map(u => u.username)
            .join(', ');
    };

    if (loading) {
        return <div className="loading">Loading projects...</div>;
    }

    return (
        <div className="page-container projects-page">
            <header className="page-header">
                <h2>My Projects</h2>
                <p>View details of the projects you are contributing to.</p>
            </header>
            
            <div className="project-grid">
                {userProjects.length > 0 ? (
                    userProjects.map(project => (
                        <div key={project.id} className="project-card">
                            <div className="project-header">
                                <h3>{project.name}</h3>
                                <span className={`status-badge ${project.status.toLowerCase().replace(' ', '-')}`}>{project.status}</span>
                            </div>
                            <p className="project-description">{project.description}</p>
                            <div className="project-members">
                                <i className="fas fa-users"></i>
                                <span>Team: {getProjectMembers(project.id)}</span>
                            </div>
                            <div className="project-deadline">
                                <i className="fas fa-calendar-alt"></i>
                                <span>Deadline: {project.deadline}</span>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>No projects assigned yet.</p>
                )}
            </div>
        </div>
    );
};

export default Projects;
