// /client/src/pages/Tasks.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Tasks.css';

const Tasks = ({ user }) => {
    const [tasks, setTasks] = useState([]);

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/users/${user.id}`);
                setTasks(response.data.tasks);
            } catch (error) {
                console.error('Error fetching tasks:', error);
            }
        };
        fetchUserData();
    }, [user.id]);
    
    // ... rest of the component logic for toggling completion ...
    const pendingTasks = tasks.filter(task => !task.completed);
    const completedTasks = tasks.filter(task => task.completed);
    
    const toggleTaskCompletion = (taskId) => {
        setTasks(tasks.map(task => 
            task.id === taskId ? { ...task, completed: !task.completed } : task
        ));
    };

    return (
        <div className="page-container tasks-page">
            <header className="page-header">
                <h2>My Tasks</h2>
                <p>Track your assignments and progress.</p>
            </header>
            
            <div className="task-board">
                <div className="task-column">
                    <h3><i className="fas fa-list-ul"></i> To Do ({pendingTasks.length})</h3>
                    <div className="task-list">
                        {pendingTasks.map(task => (
                            <div key={task.id} className="task-card">
                                <h4>{task.title}</h4>
                                <p className="task-deadline">Deadline: {task.deadline}</p>
                                <button onClick={() => toggleTaskCompletion(task.id)}>Mark as Done</button>
                            </div>
                        ))}
                    </div>
                </div>

                <div className="task-column">
                    <h3><i className="fas fa-check-circle"></i> Completed ({completedTasks.length})</h3>
                    <div className="task-list">
                        {completedTasks.map(task => (
                            <div key={task.id} className="task-card completed">
                                <h4>{task.title}</h4>
                                <p className="task-deadline">Completed!</p>
                                <button onClick={() => toggleTaskCompletion(task.id)}>Re-open</button>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Tasks;
