// /client/src/pages/Profile.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Profile.css';

const Profile = ({ user }) => {
    const [profile, setProfile] = useState(null);

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/users/${user.id}`);
                setProfile(response.data);
            } catch (error) {
                console.error('Error fetching profile:', error);
            }
        };
        fetchUserData();
    }, [user.id]);

    if (!profile) {
        return <div className="loading">Loading...</div>;
    }

    return (
        <div className="page-container profile-page">
            <header className="page-header">
                <h2>My Profile</h2>
                <p>Manage your personal information and settings.</p>
            </header>
            
            <div className="profile-content">
                <div className="profile-card">
                    <img src={profile.profilePicture} alt="Profile" className="profile-pic" />
                    <div className="profile-details">
                        <h3>{profile.username}</h3>
                        <p><strong>Role:</strong> {profile.role}</p>
                        <p><strong>Email:</strong> {profile.email}</p>
                        <p><strong>Joined:</strong> {profile.joinDate}</p>
                    </div>
                </div>
                
                <div className="profile-stats-card">
                    <h3>Performance Stats</h3>
                    <div className="stats-list">
                        <div className="stat-item">
                            <i className="fas fa-dollar-sign"></i>
                            <div>
                                <h4>Donations Raised</h4>
                                <p>â‚¹{profile.donationsRaised.toLocaleString()}</p>
                            </div>
                        </div>
                        <div className="stat-item">
                            <i className="fas fa-share-alt"></i>
                            <div>
                                <h4>Referral Count</h4>
                                <p>{profile.referralCount}</p>
                            </div>
                        </div>
                        <div className="stat-item">
                            <i className="fas fa-tasks"></i>
                            <div>
                                <h4>Completed Tasks</h4>
                                <p>{profile.tasks.filter(t => t.completed).length}</p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            
            <div className="profile-actions">
                <button>Edit Profile</button>
            </div>
        </div>
    );
};

export default Profile;
