// /client/src/pages/Leaderboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Leaderboard.css';

const Leaderboard = () => {
    const [users, setUsers] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        const fetchUsers = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/users');
                const sortedUsers = response.data.sort((a, b) => b.donationsRaised - a.donationsRaised);
                setUsers(sortedUsers);
            } catch (error) {
                console.error('Error fetching users:', error);
            }
        };
        fetchUsers();
    }, []);

    const filteredUsers = users.filter(user =>
        user.username.toLowerCase().includes(searchTerm.toLowerCase())
    );

    return (
        <div className="page-container leaderboard-page">
            <header className="page-header">
                <h2>Leaderboard</h2>
                <p>See how you rank against other interns.</p>
            </header>
            
            <div className="search-container">
                <input
                    type="text"
                    placeholder="Search by name..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
            </div>
            
            <div className="leaderboard-list">
                {filteredUsers.length > 0 ? (
                    filteredUsers.map((user, index) => (
                        <div key={user.id} className="leaderboard-item">
                            <span className="rank">{index + 1}</span>
                            <img src={user.profilePicture} alt="Profile" className="leaderboard-profile-pic" />
                            <div className="user-info">
                                <span className="username">{user.username}</span>
                                <span className="donations">â‚¹{user.donationsRaised.toLocaleString()}</span>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>No users found.</p>
                )}
            </div>
        </div>
    );
};

export default Leaderboard;
