// /client/src/pages/Dashboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Dashboard.css';

const Dashboard = ({ user }) => {
    const [userData, setUserData] = useState(null);

    useEffect(() => {
        const fetchUserData = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/users/${user.id}`);
                setUserData(response.data);
            } catch (error) {
                console.error('Error fetching user data:', error);
            }
        };
        fetchUserData();
    }, [user.id]);

    if (!userData) {
        return <div className="loading">Loading...</div>;
    }

    const goalProgress = (userData.donationsRaised / 50000) * 100;
    const progressColor = goalProgress < 50 ? 'red' : goalProgress < 100 ? 'orange' : 'green';

    return (
        <div className="page-container dashboard-page">
            <header className="page-header">
                <img src={userData.profilePicture} alt="Profile" className="profile-pic" />
                <h2>Welcome, {userData.username}!</h2>
                <p>You're doing great. Keep up the good work!</p>
            </header>

            <div className="stats-grid">
                <div className="stat-card">
                    <h3>Donations Raised</h3>
                    <p className="stat-value">₹{userData.donationsRaised.toLocaleString()}</p>
                </div>
                <div className="stat-card">
                    <h3>Referral Code</h3>
                    <p className="stat-value">{userData.referralCode}</p>
                </div>
            </div>

            <div className="progress-card">
                <h3>Donation Goal Progress</h3>
                <div className="progress-bar-container">
                    <div className="progress-bar" style={{ width: `${goalProgress}%`, backgroundColor: progressColor }}></div>
                </div>
                <p className="progress-text">{`₹${userData.donationsRaised.toLocaleString()} of ₹50,000 goal`}</p>
            </div>

            <div className="rewards-card">
                <h3>Rewards Unlocked</h3>
                <div className="rewards-list">
                    {userData.rewardsUnlocked.length > 0 ? (
                        userData.rewardsUnlocked.map((reward, index) => (
                            <span key={index} className="reward-badge">{reward}</span>
                        ))
                    ) : (
                        <p>No rewards unlocked yet.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
