// /client/src/App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Sidebar from './components/Sidebar';
import Login from './components/Login';
import Dashboard from './pages/Dashboard';
import Leaderboard from './pages/Leaderboard';
import Admin from './pages/Admin';
import Tasks from './pages/Tasks';
import Projects from './pages/Projects';
import Profile from './pages/Profile';
import Resources from './pages/Resources';

import './App.css';
import axios from 'axios';

function App() {
    const [loggedInUser, setLoggedInUser] = useState(null);

    const handleLogin = async (userId) => {
        try {
            const response = await axios.get(`http://localhost:5000/api/users/${userId}`);
            setLoggedInUser(response.data);
        } catch (error) {
            console.error("Login failed:", error);
            alert("Login failed. Please check the User ID and try again.");
        }
    };

    return (
        <Router>
            <div className="app-container">
                {loggedInUser && <Sidebar />}
                <main className={`content-container ${loggedInUser ? '' : 'shifted'}`}>
                    <Routes>
                        <Route path="/" element={loggedInUser ? <Dashboard user={loggedInUser} /> : <Login onLogin={handleLogin} />} />
                        <Route path="/dashboard" element={loggedInUser ? <Dashboard user={loggedInUser} /> : <Login onLogin={handleLogin} />} />
                        <Route path="/leaderboard" element={loggedInUser ? <Leaderboard user={loggedInUser} /> : <Login onLogin={handleLogin} />} />
                        <Route path="/tasks" element={loggedInUser ? <Tasks user={loggedInUser} /> : <Login onLogin={handleLogin} />} />
                        <Route path="/projects" element={loggedInUser ? <Projects user={loggedInUser} /> : <Login onLogin={handleLogin} />} />
                        <Route path="/profile" element={loggedInUser ? <Profile user={loggedInUser} /> : <Login onLogin={handleLogin} />} />
                        <Route path="/resources" element={loggedInUser ? <Resources /> : <Login onLogin={handleLogin} />} />
                        <Route path="/admin" element={loggedInUser ? <Admin /> : <Login onLogin={handleLogin} />} />
                    </Routes>
                </main>
            </div>
        </Router>
    );
}

export default App;
